package org.inurl.jethack;

import java.math.BigInteger;
import java.util.HashMap;
import java.util.Map;

public class BigIntegerCache {

    public static final Map<String, BigInteger[]> ARG_CACHE = new HashMap<>();
    public static final Map<String, BigInteger> RESULT_CACHE = new HashMap<>();

    static {
        addArgCache(
                "65537",
                "",
                "3",
                ""
        );
        addArgCache(
                "65537",
                "",
                "3",
                ""
        );

        addResultCache(
                "",
                "65537",
                "",
                ""
        );
        addResultCache(
                "",
                "65537",
                "",
                ""
        );
        addResultCache(
                "",
                "65537",
                "",
                ""
        );
        addResultCache(
                "",
                "65537",
                "",
                ""
        );

        // update 2023/10/09
        addResultCache(
                "",
                "65537",
                "",
                ""
        );
        addResultCache(
                "",
                "65537",
                "",
                ""
        );
    }

    private static void addArgCache(String a, String b, String c, String d) {
        ARG_CACHE.put(a + "," + b, new BigInteger[]{new BigInteger(c), new BigInteger(d)});
    }

    private static void addResultCache(String a, String b, String c, String r) {
        RESULT_CACHE.put(a + "," + b + "," + c, new BigInteger(r));
    }

}
